import Items from "../assets/Items.json";

export interface ItemsList
{
    [key: string]: Item
}

export interface Item
{
    id: string;
    name: string;
    texture: string;
}

/* This function will receive a list of string 
 * (eg: ["minecraft:stone", "minecraft:cobblestone"])
 * and will transform each string into a Item Object
 */
export const consolidate = (list: string[]): Item[] =>
{
    return list.map((item) => (Items as ItemsList)[item]);
}

export const getItemById = (id: string): Item => 
{
    return (Items as ItemsList)[id];
}

export interface Options
{
    value: string,
    label: string,
    texture: string
}

export const getItemsAsOptions = (): Options[] => Object.keys(Items).map<Options>((key, index) =>
({
    value: (Items as ItemsList)[key].id,
    label: (Items as ItemsList)[key].name,
    texture: (Items as ItemsList)[key].texture
}));

export const Missingno: string = "data:image/png;base64,";